import React from 'react';
import { motion } from 'framer-motion';

const ContainerNews = () => {
  return (
    <div className="px-4 py-4 mx-auto max-w-7xl sm:px-6 lg:px-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="relative"
      >
        {/* Background with gradient and animation */}
        <motion.div 
          className="absolute inset-0 rounded-lg bg-gradient-to-r from-cyan-500/10 via-cyan-500/5 to-transparent"
          animate={{
            opacity: [0.5, 0.8, 0.5],
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />

        {/* Main content container */}
        <motion.a
          href="https://example.com"
          target="_blank"
          rel="noopener noreferrer"
          className="relative flex items-center justify-between px-6 py-4 overflow-hidden transition-all border border-gray-700 rounded-lg bg-gray-800/50 backdrop-blur-sm hover:border-cyan-500/50 group"
          whileHover={{ scale: 1.01 }}
          whileTap={{ scale: 0.99 }}
        >
          {/* Animated highlight on hover */}
          <div className="absolute inset-0 transition-all duration-300 bg-gradient-to-r from-cyan-500/0 to-cyan-500/0 group-hover:from-cyan-500/5 group-hover:to-transparent" />

          {/* Text content */}
          <motion.div 
            className="flex-1 text-lg text-[#00E8FF] font-medium justify-center"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            RT-ACADEMY adalah platform no 1 dalam pengembangan individu dalam informatika !
          </motion.div>

          {/* Arrow icon with animation */}
          <motion.div
            className="ml-4 flex items-center gap-2 text-[#00E8FF] group"
            whileHover={{ x: 5 }}
            transition={{ type: "spring", stiffness: 400, damping: 10 }}
          >
            <svg 
              className="w-6 h-6 transition-transform duration-300 group-hover:translate-x-1" 
              viewBox="0 0 24 24" 
              fill="none" 
              xmlns="http://www.w3.org/2000/svg"
            >
              <path 
                d="M4 12H20M20 12L14 6M20 12L14 18" 
                stroke="currentColor" 
                strokeWidth="2" 
                strokeLinecap="round" 
                strokeLinejoin="round"
              />
            </svg>
          </motion.div>

          {/* Optional: Pulsing dot indicator */}
          <motion.div
            className="absolute top-4 left-4 w-2 h-2 bg-[#00E8FF] rounded-full"
            animate={{
              scale: [1, 1.2, 1],
              opacity: [1, 0.8, 1],
            }}
            transition={{
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        </motion.a>
      </motion.div>
    </div>
  );
};

export default ContainerNews;
