import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  User, CreditCard, Target, Award, 
  LogOut, Trash2, Book, GraduationCap,
  Edit, Calendar, Clock, Star, CheckCircle,
  FileText, Zap, ArrowRight, BookOpen
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const SettingsPage = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('account');

  // Tabs Configuration
  const tabs = [
    {
      id: 'account',
      label: 'Profile',
      icon: User,
      color: 'cyan'
    },
    {
      id: 'learning',
      label: 'Learning Path',
      icon: Target,
      color: 'blue'
    },
    {
      id: 'billing',
      label: 'Subscription',
      icon: CreditCard,
      color: 'purple'
    },
    {
      id: 'achievements',
      label: 'Achievements',
      icon: Award,
      color: 'yellow'
    }
  ];

  // User Data State
  const [userData, setUserData] = useState({
    profile: {
      fullName: 'John Doe',
      username: 'johndoe',
      email: 'john@example.com',
      role: 'Student',
      avatar: 'https://ui-avatars.com/api/?name=John+Doe&background=0A1525&color=fff',
      bio: 'Frontend Developer | Learning Enthusiast',
      interests: ['Frontend', 'UI/UX', 'React'],
      socialLinks: {
        github: 'github.com/johndoe',
        linkedin: 'linkedin.com/in/johndoe'
      }
    },
    learningPath: {
      currentPath: 'Frontend Development',
      progress: 65,
      completedCourses: 3,
      totalCourses: 12,
      currentCourse: 'Advanced React',
      nextMilestone: 'React Testing',
      learningStreak: 7,
      totalHours: 45.5
    },
    subscription: {
      plan: 'Pro',
      status: 'Active',
      nextBilling: '2024-08-01',
      price: '$29.99',
      features: [
        'Unlimited Access to Courses',
        'Premium Projects',
        'Certificate Generation',
        'Priority Support'
      ]
    },
    achievements: {
      totalBadges: 12,
      completedBadges: 5,
      recentAchievements: [
        {
          title: 'Frontend Master',
          description: 'Completed Frontend Fundamentals',
          date: '2024-07-15',
          icon: Book
        },
        {
          title: 'Consistent Learner',
          description: '7 Days Streak',
          date: '2024-07-14',
          icon: Zap
        }
      ],
      certificates: [
        {
          title: 'React Development',
          issueDate: '2024-06-01',
          credential: 'CERT-001'
        }
      ]
    }
  });

  const handleInputChange = (section, field, value) => {
    setUserData(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value
      }
    }));
  };

  return (
    <div className="min-h-screen bg-[#0A1525] text-white relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute rounded-full top-1/4 left-1/4 w-96 h-96 bg-cyan-500/20 blur-3xl animate-pulse"></div>
        <div className="absolute delay-1000 rounded-full bottom-1/4 right-1/4 w-96 h-96 bg-blue-500/20 blur-3xl animate-pulse"></div>
        <div className="absolute inset-0 bg-[linear-gradient(rgba(6,182,212,0.05)_1px,transparent_1px),linear-gradient(90deg,rgba(6,182,212,0.05)_1px,transparent_1px)] bg-[size:44px_44px]"></div>
      </div>

      {/* Main Container */}
      <div className="relative px-4 py-12 mx-auto max-w-7xl">
        {/* Settings Header */}
        <div className="mb-12">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="mb-2 text-3xl font-bold">Account Settings</h1>
              <p className="text-gray-400">Manage your profile and preferences</p>
            </div>
            <div className="flex gap-4">
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => navigate('/dashboard')}
                className="px-4 py-2 text-gray-400 border border-gray-700 rounded-lg hover:text-white hover:border-gray-600"
              >
                Cancel
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className="px-4 py-2 text-white rounded-lg bg-gradient-to-r from-cyan-500 to-blue-500"
              >
                Save Changes
              </motion.button>
            </div>
          </div>
        </div>

        {/* Main Content Grid */}
        <div className="grid grid-cols-12 gap-8">
          {/* Left Sidebar */}
          <div className="col-span-3 space-y-6">
            {/* Profile Quick View */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="p-6 border border-gray-700 bg-gray-800/50 backdrop-blur-sm rounded-xl"
            >
              <div className="relative group">
                <img
                  src={userData.profile.avatar}
                  alt={userData.profile.fullName}
                  className="w-20 h-20 mx-auto mb-4 border-2 rounded-xl border-cyan-500/20"
                />
                <button className="absolute inset-0 flex items-center justify-center w-full h-full transition-opacity opacity-0 rounded-xl bg-gray-900/50 group-hover:opacity-100">
                  <Edit className="w-5 h-5 text-white" />
                </button>
              </div>
              <div className="text-center">
                <h3 className="mb-1 text-lg font-semibold">{userData.profile.fullName}</h3>
                <p className="text-sm text-gray-400">{userData.profile.role}</p>
              </div>
              <div className="grid grid-cols-2 gap-4 p-4 mt-4 rounded-lg bg-gray-900/50">
                <div className="text-center">
                  <p className="text-2xl font-bold text-white">{userData.learningPath.completedCourses}</p>
                  <p className="text-xs text-gray-400">Courses</p>
                </div>
                <div className="text-center">
                  <p className="text-2xl font-bold text-white">{userData.achievements.totalBadges}</p>
                  <p className="text-xs text-gray-400">Badges</p>
                </div>
              </div>
            </motion.div>

            {/* Navigation Tabs */}
            <nav className="space-y-2">
              {tabs.map((tab) => (
                <motion.button
                  key={tab.id}
                  whileHover={{ x: 4 }}
                  onClick={() => setActiveTab(tab.id)}
                  className={`w-full flex items-center gap-3 px-4 py-3 rounded-xl transition-all ${
                    activeTab === tab.id
                      ? `bg-gradient-to-r from-${tab.color}-500/10 to-${tab.color}-500/5 text-white border border-${tab.color}-500/20`
                      : 'text-gray-400 hover:text-white hover:bg-gray-800/50'
                  }`}
                >
                  <tab.icon className="w-5 h-5" />
                  <span>{tab.label}</span>
                </motion.button>
              ))}
            </nav>

            {/* Quick Stats */}
            <div className="p-4 space-y-4 border border-gray-700 rounded-xl bg-gray-800/50">
              <h3 className="text-sm font-medium text-gray-400">Quick Stats</h3>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <Clock className="w-4 h-4 text-cyan-400" />
                    <span className="text-sm text-gray-300">Learning Time</span>
                  </div>
                  <span className="text-sm font-medium text-white">{userData.learningPath.totalHours}h</span>
                </div>
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <Calendar className="w-4 h-4 text-blue-400" />
                    <span className="text-sm text-gray-300">Streak</span>
                  </div>
                  <span className="text-sm font-medium text-white">{userData.learningPath.learningStreak} days</span>
                </div>
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <Star className="w-4 h-4 text-yellow-400" />
                    <span className="text-sm text-gray-300">Rating</span>
                  </div>
                  <span className="text-sm font-medium text-white">4.8/5.0</span>
                </div>
              </div>
            </div>
          </div>

          {/* Right Content Area */}
          <div className="col-span-9">
            <div className="p-8 border border-gray-700 bg-gray-800/50 backdrop-blur-sm rounded-xl">
              {/* Content akan berbeda berdasarkan activeTab */}
              {/* Saya akan lanjutkan dengan konten untuk masing-masing tab */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SettingsPage;
