import React from 'react';
import { StyleSheet, View, Text, TouchableOpacity } from 'react-native';

const PricingCard = ({ title, price, description, buttonText, features }) => {
  return (
    <View style={styles.card}>
      <Text style={styles.title}>{title}</Text>
      <Text style={styles.price}>{price}</Text>
      <TouchableOpacity style={styles.button}>
        <Text style={styles.buttonText}>{buttonText}</Text>
      </TouchableOpacity>
      <View style={styles.description}>
        {description.map((item, index) => (
          <View key={index} style={styles.feature}>
            <View style={styles.icon}>
              <Text style={styles.iconText}>âœ“</Text>
            </View>
            <Text style={styles.featureText}>{item}</Text>
          </View>
        ))}
      </View>
    </View>
  );
};

const Price = () => {
  const pricingData = [
    {
      title: 'PEMULA',
      price: 'Rp99.000/bulan',
      description: [
        'Akses ke Module HTML, Beginner CSS, Intermediete CSS, JS',
        '5 Beginner Friendly Project',
        'Access Ke Forum Konsultasi',
        'Sertifikat Penyelesaian Modul dan Proyek Kursus',
      ],
      buttonText: 'Mulai Sekarang',
    },
    {
      title: 'MENENGAH',
      price: 'Rp199.000/bulan',
      description: [
        'Semua Fitur Pricing Pemula',
        'Kursus React, Nodejs, Express, Intermediete CSS2',
        '10 Medium Difficulty Project',
        'Sertifikat Penyelesaian Modul dan Proyek Kursus',
      ],
      buttonText: 'Mulai Sekarang',
    },
    {
      title: 'AHLI',
      price: 'Rp299.000/bulan',
      description: [
        'Semua Fitur menengah',
        'Semua Kursus dapat di Akses',
        '15 Expert Ivl Project',
        'Sertifikat Penyelesaian Modul dan Proyek Kursus',
      ],
      buttonText : 'Mulai Sekarang',
    },
  ];

  return (
    <View style={styles.container}>
      {pricingData.map((item, index) => (
        <PricingCard
          key={index}
          title={item.title}
          price={item.price}
          description={item.description}
          buttonText={item.buttonText}
        />
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#181818',
    padding: 20,
  },
  card: {
    backgroundColor: '#282828',
    padding: 20,
    borderRadius: 10,
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#fff',
  },
  price: {
    fontSize: 32,
    fontWeight: 'bold',
    marginBottom: 20,
    color: '#fff',
  },
  button: {
    backgroundColor: '#007bff',
    padding: 15,
    borderRadius: 5,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonText: {
    fontSize: 16,
    color: '#fff',
  },
  description: {
    marginBottom: 20,
  },
  feature: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  icon: {
    marginRight: 10,
  },
  iconText: {
    fontSize: 16,
    color: '#fff',
  },
  featureText: {
    fontSize: 16,
    color: '#fff',
  },
});

export default App;
