import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import {
  Layout, Code, Beaker, ArrowRight, Terminal, 
  BookOpen, Trophy, Activity, Users, Star,
  Shield, Target, BarChart
} from 'lucide-react';

import logo from '../assets/images/logos.png';

const ChoosePlatform = () => {
  const navigate = useNavigate();

  const platforms = [
    {
      title: "Lab RT-ACADEMY",
      icon: Beaker,
      path: "/lab",
      description: "Praktik langsung dengan environment development yang aman",
      progress: 34,
      modules: [
        {
          title: "Walking An Application",
          description: "Manually review applications for security issues using browser dev tools",
          icon: Terminal,
          progress: 45
        },
        {
          title: "Content Discovery",
          description: "Learn various ways of discovering hidden content on web servers",
          icon: Shield,
          progress: 20
        },
        {
          title: "Authentication Bypass",
          description: "Learn to identify and exploit authentication vulnerabilities",
          icon: Users,
          progress: 0
        }
      ],
      stats: [
        { label: "XP", value: "4,446" },
        { label: "Rank", value: "46" },
        { label: "Completed", value: "34%" }
      ]
    },
    {
      title: "Learning Path",
      icon: BookOpen,
      path: "/academy",
      description: "Jalur pembelajaran terstruktur dengan materi interaktif",
      progress: 65,
      modules: [
        {
          title: "Frontend Development",
          description: "Master modern frontend technologies and frameworks",
          icon: Code,
          progress: 75
        },
        {
          title: "Backend Development",
          description: "Build scalable server-side applications",
          icon: Terminal,
          progress: 50
        },
        {
          title: "Database Design",
          description: "Learn database modeling and optimization",
          icon: BarChart,
          progress: 30
        }
      ],
      stats: [
        { label: "Courses", value: "12" },
        { label: "Hours", value: "24.5" },
        { label: "Projects", value: "8" }
      ]
    },
    {
      title: "School Dashboard",
      icon: Layout,
      path: "/school",
      description: "Manajemen pembelajaran dan analitik untuk institusi",
      progress: 89,
      modules: [
        {
          title: "Class Management",
          description: "Organize and track student progress effectively",
          icon: Users,
          progress: 90
        },
        {
          title: "Analytics Dashboard",
          description: "Comprehensive insights into learning metrics",
          icon: BarChart,
          progress: 85
        },
        {
          title: "Resource Center",
          description: "Centralized learning materials and resources",
          icon: BookOpen,
          progress: 70
        }
      ],
      stats: [
        { label: "Students", value: "250+" },
        { label: "Classes", value: "12" },
        { label: "Teachers", value: "8" }
      ]
    }
  ];

  return (
    <div className="min-h-screen bg-[#0A1525] text-white relative overflow-hidden">
      {/* Background Effects */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute rounded-full top-1/4 left-1/4 w-96 h-96 bg-cyan-500/20 blur-3xl animate-pulse"/>
        <div className="absolute rounded-full bottom-1/4 right-1/4 w-96 h-96 bg-blue-500/20 blur-3xl animate-pulse"/>
        <div className="absolute inset-0 bg-[linear-gradient(rgba(6,182,212,0.05)_1px,transparent_1px),linear-gradient(90deg,rgba(6,182,212,0.05)_1px,transparent_1px)] bg-[size:44px_44px]"/>
      </div>

      <div className="relative px-6 py-8">
        {/* Header Section */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="max-w-6xl mx-auto mb-12 text-center"
        >
          <img src={logo} alt="RT Academy" className="h-12 mx-auto mb-8"/>
          <h1 className="mb-4 text-4xl font-bold">Choose Your Learning Path</h1>
          <p className="text-xl text-gray-400">Select a platform to continue your journey</p>
        </motion.div>

        {/* Stats Overview */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="max-w-6xl p-6 mx-auto mb-12 border border-gray-700 rounded-xl bg-gray-800/50 backdrop-blur-sm"
        >
          <div className="grid grid-cols-5 gap-8">
            <div className="col-span-2 p-4">
              <div className="flex items-center gap-4">
                <div className="relative">
                  <img 
                    src="/api/placeholder/100/100"
                    alt="Profile" 
                    className="w-20 h-20 border-2 rounded-xl border-cyan-500/20"
                  />
                  <div className="absolute p-2 rounded-lg -bottom-2 -right-2 bg-gradient-to-r from-cyan-500 to-blue-500">
                    <Trophy className="w-4 h-4"/>
                  </div>
                </div>
                <div>
                  <h2 className="text-2xl font-bold">Welcome back!</h2>
                  <p className="text-gray-400">Level 8 [0x8]</p>
                  <div className="w-32 h-2 mt-2 bg-gray-700 rounded-full">
                    <motion.div
                      initial={{ width: 0 }}
                      animate={{ width: '75%' }}
                      className="h-2 rounded-full bg-gradient-to-r from-cyan-500 to-blue-500"
                    />
                  </div>
                </div>
              </div>
            </div>
            <div className="grid grid-cols-4 col-span-3 gap-4">
              {[
                { icon: Target, label: 'Points', value: '46' },
                { icon: Trophy, label: 'Rank', value: '143,938' },
                { icon: Star, label: 'Completed', value: '4,446' },
                { icon: Activity, label: 'Streak', value: '10' }
              ].map((stat, index) => (
                <div key={index} className="p-4 text-center rounded-xl bg-gray-900/50">
                  <stat.icon className="w-6 h-6 mx-auto mb-2 text-cyan-400"/>
                  <div className="text-2xl font-bold">{stat.value}</div>
                  <div className="text-sm text-gray-400">{stat.label}</div>
                </div>
              ))}
            </div>
          </div>
        </motion.div>

        {/* Platform Cards */}
        <div className="max-w-6xl mx-auto">
          <div className="grid grid-cols-1 gap-8 md:grid-cols-3">
            {platforms.map((platform, index) => (
              <motion.div
                key={platform.title}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 + (index * 0.1) }}
                className="relative group"
              >
                <Card className="overflow-hidden transition-colors border border-gray-700 bg-gray-800/50 backdrop-blur-sm hover:border-cyan-500/50">
                  <CardHeader className="p-6">
                    <div className="flex items-start gap-4">
                      <div className="p-3 rounded-xl bg-gradient-to-r from-cyan-500/10 to-blue-500/10">
                        <platform.icon className="w-8 h-8 text-cyan-400"/>
                      </div>
                      <div>
                        <h3 className="text-xl font-bold">{platform.title}</h3>
                        <p className="text-gray-400">{platform.description}</p>
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent className="p-6">
                    {/* Module Progress */}
                    <div className="mb-6 space-y-4">
                      {platform.modules.map((module, idx) => (
                        <div key={idx} className="group">
                          <div className="flex items-center gap-3 mb-2">
                            <div className="p-2 rounded-lg bg-gray-900/50">
                              <module.icon className="w-4 h-4 text-cyan-400"/>
                            </div>
                            <div className="flex-1">
                              <div className="font-medium">{module.title}</div>
                              <div className="text-sm text-gray-400">{module.description}</div>
                            </div>
                          </div>
                          {module.progress > 0 && (
                            <div className="h-1 bg-gray-700 rounded-full">
                              <motion.div
                                initial={{ width: 0 }}
                                animate={{ width: `${module.progress}%` }}
                                className="h-1 rounded-full bg-gradient-to-r from-cyan-500 to-blue-500"
                              />
                            </div>
                          )}
                        </div>
                      ))}
                    </div>

                    {/* Stats */}
                    <div className="grid grid-cols-3 gap-4 mb-6">
                      {platform.stats.map((stat, idx) => (
                        <div key={idx} className="p-2 text-center rounded-lg bg-gray-900/50">
                          <div className="text-lg font-bold">{stat.value}</div>
                          <div className="text-xs text-gray-400">{stat.label}</div>
                        </div>
                      ))}
                    </div>

                    {/* Action Button */}
                    <motion.button
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                      onClick={() => navigate(platform.path)}
                      className="flex items-center justify-center w-full gap-2 px-4 py-3 font-medium text-white transition-opacity rounded-lg bg-gradient-to-r from-cyan-500 to-blue-500 hover:opacity-90"
                    >
                      Enter Platform
                      <ArrowRight className="w-4 h-4"/>
                    </motion.button>
                  </CardContent>
                </Card>
              </motion.div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChoosePlatform;
