import React, { useState } from 'react';
import cardimg from ''

const SolutionsLayout = () => {
  const [activeTeam, setActiveTeam] = useState('red');

  const teamContent = {
    red: {
      title: "Master offensive strategies to enable effective defensive operations.",
      description: "For modern, 360° cyber professionals and organizations.",
      features: [
        "CVE-based vulnerable labs",
        "Realistic enterprise scenarios",
        "Real-time attack/defense simulation",
        "Extensive mapping and reporting"
      ]
    },
    blue: {
      title: "Defend your infrastructure with comprehensive security practices.",
      description: "For security teams focused on protecting enterprise systems.",
      features: [
        "Defense-in-depth training",
        "Incident response scenarios",
        "Threat detection labs",
        "Security tool mastery"
      ]
    },
    purple: {
      title: "Combine offensive and defensive techniques for complete coverage.",
      description: "For organizations seeking comprehensive security solutions.",
      features: [
        "Full-spectrum scenarios",
        "Advanced persistence handling",
        "Team collaboration exercises",
        "Strategic planning tools"
      ]
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 p-8">
      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Left Column */}
        <div className="space-y-6">
          <h1 className="text-5xl font-bold leading-tight">
            Solutions for all cybersecurity domains.
          </h1>
          
          <div className="space-y-4">
            <button 
              onClick={() => setActiveTeam('red')}
              className={`w-full text-left p-4 rounded-lg transition-all duration-300 ${
                activeTeam === 'red' 
                  ? 'bg-gray-800 border-l-4 border-red-500' 
                  : 'bg-gray-800/50 hover:bg-gray-800/70'
              }`}
            >
              Red Teams
            </button>
            <button 
              onClick={() => setActiveTeam('blue')}
              className={`w-full text-left p-4 rounded-lg transition-all duration-300 ${
                activeTeam === 'blue' 
                  ? 'bg-gray-800 border-l-4 border-blue-500' 
                  : 'bg-gray-800/50 hover:bg-gray-800/70'
              }`}
            >
              Blue Teams
            </button>
            <button 
              onClick={() => setActiveTeam('purple')}
              className={`w-full text-left p-4 rounded-lg transition-all duration-300 ${
                activeTeam === 'purple' 
                  ? 'bg-gray-800 border-l-4 border-purple-500' 
                  : 'bg-gray-800/50 hover:bg-gray-800/70'
              }`}
            >
              Purple Approach
            </button>
          </div>
        </div>

        {/* Right Column */}
        <div className="space-y-6">
          {/* Image Card */}
          <div className="w-full h-64 rounded-2xl overflow-hidden">
            <img 
              src="/api/placeholder/800/400" 
              alt="Cybersecurity visualization" 
              className="w-full h-full object-cover"
            />
          </div>

          {/* Content Card */}
          <div 
            className="bg-gray-800 rounded-2xl p-8 space-y-6 transition-all duration-300"
            style={{
              opacity: 1,
              transform: 'translateY(0)',
            }}
          >
            <div className="space-y-4">
              <h2 className="text-2xl font-semibold">
                {teamContent[activeTeam].title}
              </h2>
              <p className="text-gray-300">
                {teamContent[activeTeam].description}
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {teamContent[activeTeam].features.map((feature, index) => (
                <div 
                  key={index} 
                  className="flex items-center gap-2 transition-all duration-300"
                  style={{
                    animation: `fadeIn 0.3s ease-out ${index * 0.1}s`
                  }}
                >
                  <svg
                    className="w-5 h-5 text-lime-400"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M5 13l4 4L19 7"
                    />
                  </svg>
                  <span className="text-gray-300">{feature}</span>
                </div>
              ))}
            </div>

            <button className="bg-lime-400 text-gray-900 px-6 py-2 rounded-md hover:bg-lime-500 transition-colors duration-300">
              See More
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SolutionsLayout;
