import React from 'react';
import { motion } from 'framer-motion';
import { 
  Layout, Monitor, Book, Award, Users, BookOpen, Code, FileCode, Search, Bell,
  // Tambahkan icons baru
  PlayCircle, Target, Clock, Star, TrendingUp, MessageCircle
} from 'lucide-react';
import logo from '../assets/images/logos.png';

// Tambahkan data models baru di bawah imports
const learningStats = [
  { label: 'Total Jam Belajar', value: '24.5 Jam', icon: Clock },
  { label: 'Module Selesai', value: '3/12', icon: FileCode },
  { label: 'Pencapaian', value: '4 Badges', icon: Award },
  { label: 'Streak Belajar', value: '5 Hari', icon: TrendingUp }
];

const quickActions = [
  { 
    icon: PlayCircle, 
    label: 'Lanjut Belajar', 
    description: 'Lanjutkan HTML Basics',
    color: 'cyan' 
  },
  { 
    icon: BookOpen, 
    label: 'Materi Terakhir', 
    description: 'HTML Elements',
    color: 'blue' 
  },
  { 
    icon: Target, 
    label: 'Goal Harian', 
    description: '2/3 Modules completed',
    color: 'green' 
  },
  { 
    icon: MessageCircle, 
    label: 'Forum Diskusi', 
    description: '3 new messages',
    color: 'purple' 
  }
];

const achievements = [
  {
    title: 'HTML Master',
    description: 'Selesaikan semua modul HTML',
    progress: 25,
    icon: Star,
    color: 'yellow'
  },
  {
    title: 'Consistent Learner',
    description: '7 hari streak belajar',
    progress: 71,
    icon: TrendingUp,
    color: 'green'
  }
];
