import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  Mail, 
  ChevronRight, 
  Facebook, 
  Twitter, 
  Instagram, 
  Linkedin,
  Github
} from 'lucide-react';

const Footer = () => {
  const [email, setEmail] = useState('');
  
  const footerLinks = {
    Products: ['Courses', 'Workshops', 'Certifications', 'Live Sessions'],
    Company: ['About Us', 'Careers', 'Partners', 'Blog'],
    Resources: ['Documentation', 'Tutorial', 'Community', 'FAQ'],
    Legal: ['Privacy Policy', 'Terms of Service', 'Cookie Policy']
  };

  const socialIcons = [
    { Icon: Facebook, href: '#' },
    { Icon: Twitter, href: '#' },
    { Icon: Instagram, href: '#' },
    { Icon: Linkedin, href: '#' },
    { Icon: Github, href: '#' }
  ];

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        staggerChildren: 0.1
      }
    }
  };

  return (
    <footer className="bg-[#0A1525] text-white">
      <motion.div 
        className="px-4 pt-16 pb-8 mx-auto max-w-7xl"
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        variants={containerVariants}
      >
        {/* Newsletter Section */}
        <motion.div 
          className="p-8 mb-16 border border-gray-800 rounded-2xl bg-gradient-to-r from-cyan-500/10 to-blue-500/10 backdrop-blur-sm"
          variants={containerVariants}
        >
          <div className="max-w-2xl mx-auto text-center">
            <h3 className="mb-4 text-2xl font-bold">Join RT-ACADEMY Newsletter</h3>
            <p className="mb-6 text-gray-400">
              Stay updated with the latest web development trends, tutorials, and exclusive content
            </p>
            <div className="flex max-w-md gap-4 mx-auto">
              <div className="relative flex-1">
                <Mail className="absolute text-gray-400 transform -translate-y-1/2 left-3 top-1/2" size={20} />
                <input
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full py-3 pl-10 pr-4 transition-colors border border-gray-800 rounded-lg bg-white/5 focus:outline-none focus:border-cyan-500"
                />
              </div>
              <button className="px-6 py-3 bg-gradient-to-r from-[#00E8FF] to-[#0077FF] rounded-lg font-medium hover:opacity-90 transition-opacity">
                Subscribe
              </button>
            </div>
          </div>
        </motion.div>

        {/* Main Grid Section */}
        <div className="grid grid-cols-1 gap-8 mb-16 md:grid-cols-2 lg:grid-cols-6">
          {/* Company Info */}
          <motion.div 
            className="lg:col-span-2"
            variants={containerVariants}
          >
            <h2 className="mb-4 text-2xl font-bold">RT-ACADEMY</h2>
            <p className="mb-6 text-gray-400">
              Empowering the next generation of web developers through comprehensive learning experiences and practical skills development.
            </p>
            <div className="flex gap-4">
              {socialIcons.map(({ Icon, href }, index) => (
                <motion.a
                  key={index}
                  href={href}
                  className="p-2 transition-colors rounded-lg bg-white/5 hover:bg-white/10"
                  whileHover={{ scale: 1.1, rotate: 10 }}
                >
                  <Icon size={20} />
                </motion.a>
              ))}
            </div>
          </motion.div>

          {/* Link Columns */}
          {Object.entries(footerLinks).map(([title, links]) => (
            <motion.div key={title} variants={containerVariants}>
              <h3 className="mb-4 font-semibold">{title}</h3>
              <ul className="space-y-3">
                {links.map((link, index) => (
                  <motion.li key={index}>
                    <a 
                      href="#" 
                      className="flex items-center text-gray-400 hover:text-white group"
                    >
                      <span>{link}</span>
                      <ChevronRight 
                        size={16} 
                        className="ml-1 transition-opacity opacity-0 group-hover:opacity-100"
                      />
                    </a>
                  </motion.li>
                ))}
              </ul>
            </motion.div>
          ))}
        </div>

        {/* Bottom Section */}
        <motion.div 
          className="flex flex-col items-center justify-between gap-4 pt-8 border-t border-gray-800 md:flex-row"
          variants={containerVariants}
        >
          <p className="text-sm text-gray-400">
            © 2024 RT-ACADEMY. All rights reserved.
          </p>
          <div className="flex gap-6 text-sm text-gray-400">
            <a href="#" className="transition-colors hover:text-white">Privacy Policy</a>
            <a href="#" className="transition-colors hover:text-white">Terms of Service</a>
            <a href="#" className="transition-colors hover:text-white">Cookie Policy</a>
          </div>
        </motion.div>
      </motion.div>
    </footer>
  );
};

export default Footer;
