import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const TestimonialsSection = () => {
  const testimonials = [
    {
      text: "Our team has seen remarkable growth since implementing this platform. The practical training materials and hands-on approach have accelerated our learning process significantly. We've implemented a monthly recognition program to celebrate top performers.",
      author: "Taylor Chen",
      role: "Technical Director, TechCorp",
      initials: "TC",
      bgColor: "bg-blue-500"
    },
    {
      text: "The training content is consistently updated with cutting-edge material, keeping our skills relevant in an ever-evolving industry. The variety of technical challenges has helped our team develop a comprehensive skill set.",
      author: "Alex Smith",
      role: "Security Lead, SecureStack",
      initials: "AS",
      bgColor: "bg-green-500"
    },
    {
      text: "This platform has transformed how we approach security training. The gamified elements keep our team engaged while the depth of content ensures they're learning valuable skills.",
      author: "Maria Rodriguez",
      role: "CISO, DataGuard",
      initials: "MR",
      bgColor: "bg-purple-500"
    },
    {
      text: "We've seen a significant improvement in our team's problem-solving abilities since implementing this solution. The real-world scenarios are particularly valuable.",
      author: "James Wilson",
      role: "Security Manager, CloudSecure",
      initials: "JW",
      bgColor: "bg-orange-500"
    },
    {
      text: "The quality and frequency of updates ensure our team stays ahead of emerging threats. The platform's structure makes complex concepts accessible without oversimplifying.",
      author: "Sarah Park",
      role: "Training Lead, CyberShield",
      initials: "SP",
      bgColor: "bg-red-500"
    },
    {
      text: "The breadth of content and practical exercises have made this an essential part of our security training program. Our team's capabilities have grown tremendously.",
      author: "David Kumar",
      role: "InfoSec Director, NetDefend",
      initials: "DK",
      bgColor: "bg-teal-500"
    }
  ];

  const [currentPage, setCurrentPage] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const testimonialsPerPage = 2;
  const totalPages = Math.ceil(testimonials.length / testimonialsPerPage);

  const handlePageChange = (newPage) => {
    setIsAnimating(true);
    setTimeout(() => {
      setCurrentPage(newPage);
      setTimeout(() => {
        setIsAnimating(false);
      }, 50);
    }, 300);
  };

  const nextSlide = () => {
    const newPage = (currentPage + 1) % totalPages;
    handlePageChange(newPage);
  };

  const prevSlide = () => {
    const newPage = (currentPage - 1 + totalPages) % totalPages;
    handlePageChange(newPage);
  };

  const getCurrentTestimonials = () => {
    const start = currentPage * testimonialsPerPage;
    return testimonials.slice(start, start + testimonialsPerPage);
  };

  return (
    <div className="px-8 py-16 text-white ">
      {/* Main heading section */}
      <div className="mx-auto mb-16 max-w-7xl">
        <h1 className="mb-4 text-5xl font-bold">
          Dipercaya Oleh
        </h1>
        <h1 className="text-5xl font-bold">
          Valued by teams.
        </h1>
      </div>

      {/* Testimonials grid */}
      <div className="mx-auto max-w-7xl">
        <div className="grid gap-8 md:grid-cols-2">
          {getCurrentTestimonials().map((testimonial, index) => (
            <div 
              key={currentPage * testimonialsPerPage + index} 
              className={`bg-slate-800/50 rounded-lg p-8 transform transition-all duration-500 ease-in-out ${
                isAnimating ? 'opacity-0 translate-y-4' : 'opacity-100 translate-y-0'
              }`}
            >
              <p className="mb-8 text-lg text-slate-300">
                "{testimonial.text}"
              </p>
              
              <div className="flex items-center">
                <div className={`w-12 h-12 ${testimonial.bgColor} rounded-lg mr-4 flex items-center justify-center`}>
                  <span className="text-xl font-bold">{testimonial.initials}</span>
                </div>
                <div>
                  <p className="font-medium">{testimonial.author}</p>
                  <p className="text-slate-400">{testimonial.role}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Navigation buttons and indicators */}
      <div className="flex items-center justify-between mx-auto mt-8 max-w-7xl">
        <div className="flex gap-2">
          {[...Array(totalPages)].map((_, index) => (
            <button
              key={index}
              className={`w-2 h-2 rounded-full transition-all duration-300 ${
                currentPage === index 
                  ? 'bg-white w-4' 
                  : 'bg-slate-600'
              }`}
              onClick={() => handlePageChange(index)}
              disabled={isAnimating}
            />
          ))}
        </div>
        <div className="flex gap-2">
          <button 
            className="p-2 transition-colors rounded-lg bg-slate-800 hover:bg-slate-700 disabled:opacity-50"
            onClick={prevSlide}
            disabled={isAnimating}
          >
            <ChevronLeft className="w-6 h-6" />
          </button>
          <button 
            className="p-2 transition-colors rounded-lg bg-slate-800 hover:bg-slate-700 disabled:opacity-50"
            onClick={nextSlide}
            disabled={isAnimating}
          >
            <ChevronRight className="w-6 h-6" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default TestimonialsSection;
