import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import logo from '../../assets/images/logos.png';

function Navbar() {
    const [scrolled, setScrolled] = useState(false);
    const location = useLocation();
    
    // Handle navbar background on scroll
    useEffect(() => {
        const handleScroll = () => {
            setScrolled(window.scrollY > 20);
        };
        
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const navLinks = [
        { to: "/produk", label: "Produk" },
        { to: "/tujuan", label: "Tujuan" },
        { to: "/harga", label: "Harga" },
        { to: "/resources", label: "Resources" },
        { to: "/perusahaan", label: "Perusahaan" }
    ];

    return (
        <motion.nav
            initial={{ y: -100 }}
            animate={{ y: 0 }}
            transition={{ duration: 0.5 }}
            className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
                scrolled 
                ? 'bg-gray-900/95 backdrop-blur-md shadow-lg' 
                : 'bg-transparent'
            }`}
        >
            <div className="px-4 mx-auto max-w-7xl sm:px-6 lg:px-8">
                <div className="flex items-center justify-between h-16">
                    {/* Logo */}
                    <motion.div
                        whileHover={{ scale: 1.05 }}
                        transition={{ duration: 0.2 }}
                    >
                        <Link to="/" className="flex items-center">
                            <img 
                                src={logo} 
                                alt="RT ACADEMY Logo" 
                                className="w-auto h-8"
                            />
                        </Link>
                    </motion.div>

                    {/* Main Navigation Links */}
                    <div className="items-center hidden space-x-1 md:flex">
                        {navLinks.map((link) => (
                            <Link
                                key={link.to}
                                to={link.to}
                                className="relative px-3 py-2 text-sm text-gray-300 transition-colors duration-200 hover:text-white"
                            >
                                {location.pathname === link.to && (
                                    <motion.div
                                        layoutId="navbar-indicator"
                                        className="absolute inset-0 bg-gray-800 rounded-lg"
                                        style={{ zIndex: -1 }}
                                        transition={{ type: "spring", bounce: 0.2, duration: 0.6 }}
                                    />
                                )}
                                {link.label}
                            </Link>
                        ))}
                    </div>

                    {/* Auth Links */}
                    <div className="items-center hidden space-x-4 md:flex">
                        <Link 
                            to="/sekolah"
                            className="text-sm text-gray-300 transition-colors duration-200 hover:text-white"
                        >
                            Sekolah
                        </Link>
                        
                        <div className="w-px h-4 bg-gray-700"></div>
                        
                        <Link 
                            to="/login"
                            className="text-sm text-gray-300 transition-colors duration-200 hover:text-white"
                        >
                            Login
                        </Link>
                        
                        <motion.div
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            <Link
                                to="/mulai"
                                className="px-4 py-2 text-sm font-medium text-white transition-opacity duration-200 rounded-lg bg-gradient-to-r from-cyan-500 to-blue-500 hover:opacity-90"
                            >
                                Mulai
                            </Link>
                        </motion.div>
                    </div>

                    {/* Mobile Menu Button */}
                    <motion.button
                        whileTap={{ scale: 0.95 }}
                        className="p-2 text-gray-400 rounded-lg md:hidden hover:text-white hover:bg-gray-800"
                    >
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                        </svg>
                    </motion.button>
                </div>
            </div>
            
            {/* Progress Bar */}
            <motion.div
                initial={{ scaleX: 0 }}
                animate={{ scaleX: scrolled ? 1 : 0 }}
                transition={{ duration: 0.3 }}
                className="h-[2px] bg-gradient-to-r from-cyan-500 to-blue-500 origin-left"
            />
        </motion.nav>
    );
}

export default Navbar;
