import React from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import { 
  School, Book, Beaker, ArrowRight, Users, Code, Terminal,
  Crown, Settings, HelpCircle, MessageSquare, User,
  Shield, CreditCard, Bell, FileText, PlayCircle,
  Zap, Target, Award, BarChart, Rocket, ChevronRight
} from 'lucide-react';
import logo from "../assets/images/logos.png";

const ChoosePlatform = () => {
  const navigate = useNavigate();

  const userData = {
    name: "John Doe",
    role: "Student",
    avatar: "https://ui-avatars.com/api/?name=John+Doe&background=0A1525&color=fff",
    stats: {
      hoursLearned: "24.5",
      coursesCompleted: "4",
      currentStreak: "7",
      certificates: "2"
    },
    currentCourse: {
      title: "Frontend Development",
      progress: 65,
      nextLesson: "React Components"
    }
  };

  const platforms = [
    {
      id: "school",
      title: "Institution Dashboard",
      subtitle: "For Schools & Educational Institutions",
      description: "Manage your institution's learning experience with comprehensive analytics and student tracking",
      icon: School,
      color: "cyan",
      features: [
        "Student Progress Tracking",
        "Analytics Dashboard",
        "Curriculum Management",
        "Performance Reports"
      ],
      metrics: [
        { label: "Students", value: "250+" },
        { label: "Courses", value: "45+" },
        { label: "Success Rate", value: "92%" }
      ],
      cta: "Access School Dashboard",
      path: "/school-dashboard",
      badge: "Premium"
    },
    {
      id: "academy",
      title: "Learning Platform",
      subtitle: "For Individual Learners",
      description: "Start your coding journey with structured learning paths and interactive content",
      icon: Book,
      color: "blue",
      features: [
        "Interactive Courses",
        "Hands-on Projects",
        "Expert Mentorship",
        "Real-world Applications"
      ],
      metrics: [
        { label: "Courses", value: "120+" },
        { label: "Projects", value: "85+" },
        { label: "Success Rate", value: "89%" }
      ],
      cta: "Start Learning",
      path: "/academy",
      badge: "Recommended"
    },
    {
      id: "lab",
      title: "Practice Lab",
      subtitle: "For Hands-on Practice",
      description: "Apply your knowledge in a safe, real-world development environment",
      icon: Beaker,
      color: "purple",
      features: [
        "Live Coding Environment",
        "Project Workspaces",
        "Debug Tools",
        "Instant Feedback"
      ],
      metrics: [
        { label: "Exercises", value: "200+" },
        { label: "Templates", value: "50+" },
        { label: "Challenges", value: "100+" }
      ],
      cta: "Access Lab",
      path: "/lab"
    }
  ];

  const quickLinks = [
    {
      title: "Continue Learning",
      description: "Frontend Development Basics",
      progress: 65,
      icon: Code,
      color: "cyan"
    },
    {
      title: "Next Milestone",
      description: "Complete React Fundamentals",
      icon: Target,
      color: "blue"
    },
    {
      title: "Latest Achievement",
      description: "CSS Master Badge Earned",
      icon: Award,
      color: "purple"
    }
  ];

  return (
    <div className="min-h-screen bg-[#0A1525] text-white">
      {/* Top Navigation */}
      <nav className="fixed top-0 left-0 right-0 z-50 border-b border-gray-800 bg-gray-900/50 backdrop-blur-xl">
        <div className="px-6 mx-auto max-w-7xl">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center gap-8">
              <motion.img
                src={logo}
                alt="RT Academy"
                className="h-8"
                whileHover={{ scale: 1.05 }}
              />
              <div className="items-center hidden gap-6 md:flex">
                <a href="#" className="text-gray-400 hover:text-white">Courses</a>
                <a href="#" className="text-gray-400 hover:text-white">Resources</a>
                <a href="#" className="text-gray-400 hover:text-white">Community</a>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="relative p-2 text-gray-400 hover:text-white"
              >
                <Bell className="w-5 h-5" />
                <span className="absolute top-0 right-0 w-2 h-2 rounded-full bg-cyan-500"></span>
              </motion.button>
              <motion.div
                whileHover={{ scale: 1.02 }}
                className="flex items-center gap-3 p-1.5 border border-gray-700 rounded-lg cursor-pointer hover:border-gray-600"
              >
                <img
                  src={userData.avatar}
                  alt={userData.name}
                  className="w-8 h-8 rounded-lg"
                />
                <span className="text-sm font-medium">{userData.name}</span>
              </motion.div>
            </div>
          </div>
        </div>
      </nav>

      {/* Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute rounded-full top-1/4 left-1/4 w-96 h-96 bg-cyan-500/20 blur-3xl animate-pulse"></div>
        <div className="absolute delay-1000 rounded-full bottom-1/4 right-1/4 w-96 h-96 bg-blue-500/20 blur-3xl animate-pulse"></div>
        <div className="absolute inset-0 bg-[linear-gradient(rgba(6,182,212,0.05)_1px,transparent_1px),linear-gradient(90deg,rgba(6,182,212,0.05)_1px,transparent_1px)] bg-[size:44px_44px]"></div>
      </div>

      {/* Main Content */}
      <div className="relative px-6 pt-20 pb-12">
        <div className="mx-auto max-w-7xl">
          {/* Welcome Section */}
          <div className="mb-12 text-center">
            <motion.h1
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="mb-4 text-4xl font-bold"
            >
              Choose Your Learning Experience
            </motion.h1>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 }}
              className="max-w-2xl mx-auto text-lg text-gray-400"
            >
              Select the platform that best suits your learning needs and goals
            </motion.p>
          </div>

          {/* Quick Links */}
          <div className="grid grid-cols-1 gap-6 mb-12 md:grid-cols-3">
            {quickLinks.map((link, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2 + index * 0.1 }}
                className="p-6 transition-all border border-gray-700 rounded-xl bg-gray-800/50 backdrop-blur-sm hover:border-cyan-500/30"
              >
                <div className={`p-3 rounded-xl bg-${link.color}-500/10 w-fit mb-4`}>
                  <link.icon className={`w-6 h-6 text-${link.color}-400`} />
                </div>
                <h3 className="mb-2 text-lg font-semibold">{link.title}</h3>
                <p className="mb-4 text-gray-400">{link.description}</p>
                {link.progress && (
                  <div className="relative h-2 overflow-hidden bg-gray-700 rounded-full">
                    <motion.div
                      initial={{ width: 0 }}
                      animate={{ width: `${link.progress}%` }}
                      transition={{ duration: 1, delay: 0.5 }}
                      className="absolute inset-y-0 left-0 bg-gradient-to-r from-cyan-500 to-blue-500"
                    />
                  </div>
                )}
              </motion.div>
            ))}
          </div>

          {/* Platform Cards */}
          <div className="grid grid-cols-1 gap-8 mb-12 lg:grid-cols-3">
            {platforms.map((platform, index) => (
              <motion.div
                key={platform.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 + index * 0.1 }}
                className="relative group"
              >
                <div className="absolute inset-0 transition-transform duration-300 transform bg-gradient-to-r from-cyan-500/10 to-blue-500/10 rounded-2xl group-hover:scale-105" />
                <div className="relative p-6 transition-all border border-gray-700 rounded-2xl bg-gray-800/50 backdrop-blur-sm hover:border-cyan-500/30">
                  {platform.badge && (
                    <span className={`absolute top-4 right-4 px-3 py-1 text-xs font-medium rounded-full ${
                      platform.badge === 'Premium' ? 'bg-cyan-500/10 text-cyan-400' : 'bg-green-500/10 text-green-400'
                    }`}>
                      {platform.badge}
                    </span>
                  )}
                  
                  <div className={`p-4 rounded-xl bg-${platform.color}-500/10 w-fit mb-6`}>
                    <platform.icon className={`w-8 h-8 text-${platform.color}-400`} />
                  </div>

                  <h3 className="mb-2 text-xl font-bold">{platform.title}</h3>
                  <p className="mb-4 text-sm text-cyan-400">{platform.subtitle}</p>
                  <p className="mb-6 text-gray-400">{platform.description}</p>

                  <div className="grid grid-cols-3 gap-4 mb-6">
                    {platform.metrics.map((metric, idx) => (
                      <div key={idx} className="p-3 text-center rounded-lg bg-gray-900/50">
                        <p className="text-lg font-bold text-white">{metric.value}</p>
                        <p className="text-xs text-gray-400">{metric.label}</p>
                      </div>
                    ))}
                  </div>

                  <div className="mb-6 space-y-3">
                    {platform.features.map((feature, idx) => (
                      <div key={idx} className="flex items-center gap-2 text-gray-300">
                        <Zap className="w-4 h-4 text-cyan-400" />
                        <span>{feature}</span>
                      </div>
                    ))}
                  </div>

                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={() => navigate(platform.path)}
                    className="flex items-center justify-center w-full gap-2 px-6 py-3 font-medium transition-opacity rounded-lg bg-gradient-to-r from-cyan-500 to-blue-500 hover:opacity-90"
                  >
                    {platform.cta}
                    <ChevronRight className="w-4 h-4" />
                  </motion.button>
                </div>
              </motion.div>
            ))}
          </div>

          {/* Help Section */}
          <div className="flex justify-center gap-6">
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              className="flex items-center gap-2 px-4 py-2 text-sm text-gray-400 border border-gray-700 rounded-lg hover:text-white hover:border-gray-600"
            >
              <HelpCircle className="w-4 h-4" />
              Help Center
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              className="flex items-center gap-2 px-4 py-2 text-sm text-gray-400 border border-gray-700 rounded-lg hover:text-white hover:border-gray-600"
            >
              <MessageSquare className="w-4 h-4" />
              Contact Support
            </motion.button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChoosePlatform;
