import React from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import { 
  School, Book, Beaker, ArrowRight, Users, Code, Terminal,
  Crown, Settings, HelpCircle, MessageSquare, User,
  Shield, CreditCard, Bell, FileText, PlayCircle,
  Zap, Target, Award, BarChart, Rocket, ChevronRight
} from 'lucide-react';
import logo from "../assets/images/logos.png";
import { useState } from 'react'; 

const ChoosePlatform = () => {
  const navigate = useNavigate();

  const userData = {
    name: "John Doe",
    role: "Student",
    avatar: "https://ui-avatars.com/api/?name=John+Doe&background=0A1525&color=fff",
    stats: {
      hoursLearned: "24.5",
      coursesCompleted: "4",
      currentStreak: "7",
      certificates: "2"
    },
    currentCourse: {
      title: "Frontend Development",
      progress: 65,
      nextLesson: "React Components"
    }
  };

  const platforms = [
    {
      id: "school",
      title: "Institution Dashboard",
      subtitle: "For Schools & Educational Institutions",
      description: "Manage your institution's learning experience with comprehensive analytics and student tracking",
      icon: School,
      color: "cyan",
      features: [
        "Student Progress Tracking",
        "Analytics Dashboard",
        "Curriculum Management",
        "Performance Reports"
      ],
      metrics: [
        { label: "Students", value: "250+" },
        { label: "Courses", value: "45+" },
        { label: "Success Rate", value: "92%" }
      ],
      cta: "Access School Dashboard",
      path: "/school-dashboard",
      badge: "Premium"
    },
    {
      id: "academy",
      title: "Learning Platform",
      subtitle: "For Individual Learners",
      description: "Start your coding journey with structured learning paths and interactive content",
      icon: Book,
      color: "blue",
      features: [
        "Interactive Courses",
        "Hands-on Projects",
        "Expert Mentorship",
        "Real-world Applications"
      ],
      metrics: [
        { label: "Courses", value: "120+" },
        { label: "Projects", value: "85+" },
        { label: "Success Rate", value: "89%" }
      ],
      cta: "Start Learning",
      path: "/academy",
      badge: "Recommended"
    },
    {
      id: "lab",
      title: "Practice Lab",
      subtitle: "For Hands-on Practice",
      description: "Apply your knowledge in a safe, real-world development environment",
      icon: Beaker,
      color: "purple",
      features: [
        "Live Coding Environment",
        "Project Workspaces",
        "Debug Tools",
        "Instant Feedback"
      ],
      metrics: [
        { label: "Exercises", value: "200+" },
        { label: "Templates", value: "50+" },
        { label: "Challenges", value: "100+" }
      ],
      cta: "Access Lab",
      path: "/lab"
    }
  ];

  const quickLinks = [
    {
      title: "Continue Learning",
      description: "Frontend Development Basics",
      progress: 65,
      icon: Code,
      color: "cyan"
    },
    {
      title: "Next Milestone",
      description: "Complete React Fundamentals",
      icon: Target,
      color: "blue"
    },
    {
      title: "Latest Achievement",
      description: "CSS Master Badge Earned",
      icon: Award,
      color: "purple"
    }
  ];

  return (
    <div className="min-h-screen bg-[#0A1525] text-white">
      {/* Simplified Navbar */}
      <nav className="fixed top-0 left-0 right-0 z-50 border-b border-gray-800 bg-gray-900/50 backdrop-blur-xl">
        <div className="px-6 mx-auto max-w-7xl">
          <div className="flex items-center justify-between h-16">
            <motion.img
              src={logo}
              alt="RT Academy"
              className="h-8 cursor-pointer"
              whileHover={{ scale: 1.05 }}
              onClick={() => navigate('/')}
            />
            
            {/* Right Section - Profile & Settings Only */}
            <div className="flex items-center gap-4">
              {/* Profile Menu */}
              <div className="relative">
                <motion.div
                  className="flex items-center gap-2 cursor-pointer"
                  onClick={() => setShowProfileMenu(!showProfileMenu)}
                >
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    className="relative"
                  >
                    <img
                      src={userData.avatar}
                      alt={userData.name}
                      className="w-10 h-10 transition-colors border-2 border-gray-700 rounded-xl hover:border-cyan-500/50"
                    />
                    <div className="absolute flex items-center justify-center w-4 h-4 rounded-full -bottom-1 -right-1 bg-gradient-to-r from-cyan-500 to-blue-500">
                      <Crown className="w-3 h-3 text-white" />
                    </div>
                  </motion.div>
                </motion.div>

                {/* Profile Dropdown Menu */}
                {showProfileMenu && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    className="absolute right-0 w-64 py-2 mt-2 bg-gray-800 border border-gray-700 shadow-xl rounded-xl"
                  >
                    {/* User Info */}
                    <div className="px-4 py-3 border-b border-gray-700">
                      <p className="font-medium text-white">{userData.name}</p>
                      <p className="text-sm text-gray-400">{userData.email}</p>
                    </div>

                    {/* Quick Stats */}
                    <div className="grid grid-cols-2 gap-2 p-4 border-b border-gray-700">
                      <div className="p-2 text-center rounded-lg bg-gray-900/50">
                        <p className="text-lg font-bold text-white">{userData.stats.hoursLearned}h</p>
                        <p className="text-xs text-gray-400">Learning Time</p>
                      </div>
                      <div className="p-2 text-center rounded-lg bg-gray-900/50">
                        <p className="text-lg font-bold text-white">{userData.stats.currentStreak}d</p>
                        <p className="text-xs text-gray-400">Current Streak</p>
                      </div>
                    </div>

                    {/* Menu Items */}
                    <div className="py-2">
                      <button
                        onClick={() => navigate('/settings')}
                        className="flex items-center w-full gap-3 px-4 py-2 text-sm text-gray-300 hover:bg-gray-700/50"
                      >
                        <Settings className="w-4 h-4" />
                        Settings
                      </button>
                      <button
                        onClick={() => navigate('/profile')}
                        className="flex items-center w-full gap-3 px-4 py-2 text-sm text-gray-300 hover:bg-gray-700/50"
                      >
                        <User className="w-4 h-4" />
                        View Profile
                      </button>
                      <button
                        onClick={() => navigate('/help')}
                        className="flex items-center w-full gap-3 px-4 py-2 text-sm text-gray-300 hover:bg-gray-700/50"
                      >
                        <HelpCircle className="w-4 h-4" />
                        Help Center
                      </button>
                      <div className="pt-2 mt-2 border-t border-gray-700">
                        <button
                          onClick={() => {/* handle logout */}}
                          className="flex items-center w-full gap-3 px-4 py-2 text-sm text-red-400 hover:bg-gray-700/50"
                        >
                          <LogOut className="w-4 h-4" />
                          Sign Out
                        </button>
                      </div>
                    </div>
                  </motion.div>
                )}
              </div>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content Wrapper - Adjusted padding for new navbar */}
      <div className="relative px-6 pt-20 pb-12">
        <div className="mx-auto max-w-7xl">
          {/* Current Progress Banner - New Addition */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="p-6 mb-12 border border-gray-700 rounded-2xl bg-gray-800/50 backdrop-blur-sm"
          >
            <div className="flex flex-col items-start justify-between gap-6 md:flex-row md:items-center">
              <div>
                <h2 className="mb-2 text-2xl font-bold">Welcome back, {userData.name}! 👋</h2>
                <p className="text-gray-400">Ready to continue your learning journey?</p>
              </div>
              <div className="flex items-center gap-8">
                <div className="text-center">
                  <p className="text-3xl font-bold text-white">{userData.stats.hoursLearned}h</p>
                  <p className="text-sm text-gray-400">Total Learning</p>
                </div>
                <div className="text-center">
                  <p className="text-3xl font-bold text-white">{userData.stats.currentStreak}d</p>
                  <p className="text-sm text-gray-400">Current Streak</p>
                </div>
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => navigate('/continue-learning')}
                  className="flex items-center gap-2 px-6 py-3 font-medium rounded-lg bg-gradient-to-r from-cyan-500 to-blue-500 hover:opacity-90"
                >
                  Continue Learning
                  <ChevronRight className="w-4 h-4" />
                </motion.button>
              </div>
            </div>
          </motion.div>

          {/* Platform Cards */}
          <div className="grid grid-cols-1 gap-8 mb-12 lg:grid-cols-3">
            {platforms.map((platform, index) => (
              <motion.div
                key={platform.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 + index * 0.1 }}
                className="relative group"
              >
                <div className="absolute inset-0 transition-transform duration-300 transform bg-gradient-to-r from-cyan-500/10 to-blue-500/10 rounded-2xl group-hover:scale-105" />
                <div className="relative p-6 transition-all border border-gray-700 rounded-2xl bg-gray-800/50 backdrop-blur-sm hover:border-cyan-500/30">
                  {platform.badge && (
                    <span className={`absolute top-4 right-4 px-3 py-1 text-xs font-medium rounded-full ${
                      platform.badge === 'Premium' ? 'bg-cyan-500/10 text-cyan-400' : 'bg-green-500/10 text-green-400'
                    }`}>
                      {platform.badge}
                    </span>
                  )}
                  
                  <div className={`p-4 rounded-xl bg-${platform.color}-500/10 w-fit mb-6`}>
                    <platform.icon className={`w-8 h-8 text-${platform.color}-400`} />
                  </div>

                  <h3 className="mb-2 text-xl font-bold">{platform.title}</h3>
                  <p className="mb-4 text-sm text-cyan-400">{platform.subtitle}</p>
                  <p className="mb-6 text-gray-400">{platform.description}</p>

                  <div className="grid grid-cols-3 gap-4 mb-6">
                    {platform.metrics.map((metric, idx) => (
                      <div key={idx} className="p-3 text-center rounded-lg bg-gray-900/50">
                        <p className="text-lg font-bold text-white">{metric.value}</p>
                        <p className="text-xs text-gray-400">{metric.label}</p>
                      </div>
                    ))}
                  </div>

                  <div className="mb-6 space-y-3">
                    {platform.features.map((feature, idx) => (
                      <div key={idx} className="flex items-center gap-2 text-gray-300">
                        <Zap className="w-4 h-4 text-cyan-400" />
                        <span>{feature}</span>
                      </div>
                    ))}
                  </div>

                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={() => navigate(platform.path)}
                    className="flex items-center justify-center w-full gap-2 px-6 py-3 font-medium transition-opacity rounded-lg bg-gradient-to-r from-cyan-500 to-blue-500 hover:opacity-90"
                  >
                    {platform.cta}
                    <ChevronRight className="w-4 h-4" />
                  </motion.button>
                </div>
              </motion.div>
            ))}
          </div>

          {/* Help Section */}
          <div className="flex justify-center gap-6">
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              className="flex items-center gap-2 px-4 py-2 text-sm text-gray-400 border border-gray-700 rounded-lg hover:text-white hover:border-gray-600"
            >
              <HelpCircle className="w-4 h-4" />
              Help Center
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              className="flex items-center gap-2 px-4 py-2 text-sm text-gray-400 border border-gray-700 rounded-lg hover:text-white hover:border-gray-600"
            >
              <MessageSquare className="w-4 h-4" />
              Contact Support
            </motion.button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChoosePlatform;
