import React from 'react';
import { motion } from 'framer-motion';
import { 
  Layout, Monitor, Book, Award, Users, BookOpen, Code, FileCode, Search, Bell,
  // Tambahkan icons baru
  PlayCircle, Target, Clock, Star, TrendingUp, MessageCircle
} from 'lucide-react';
import logo from '../assets/images/logos.png';

// Tambahkan data models baru di bawah imports
const learningStats = [
  { label: 'Total Jam Belajar', value: '24.5 Jam', icon: Clock },
  { label: 'Module Selesai', value: '3/12', icon: FileCode },
  { label: 'Pencapaian', value: '4 Badges', icon: Award },
  { label: 'Streak Belajar', value: '5 Hari', icon: TrendingUp }
];

const quickActions = [
  { 
    icon: PlayCircle, 
    label: 'Lanjut Belajar', 
    description: 'Lanjutkan HTML Basics',
    color: 'cyan' 
  },
  { 
    icon: BookOpen, 
    label: 'Materi Terakhir', 
    description: 'HTML Elements',
    color: 'blue' 
  },
  { 
    icon: Target, 
    label: 'Goal Harian', 
    description: '2/3 Modules completed',
    color: 'green' 
  },
  { 
    icon: MessageCircle, 
    label: 'Forum Diskusi', 
    description: '3 new messages',
    color: 'purple' 
  }
];

const achievements = [
  {
    title: 'HTML Master',
    description: 'Selesaikan semua modul HTML',
    progress: 25,
    icon: Star,
    color: 'yellow'
  },
  {
    title: 'Consistent Learner',
    description: '7 hari streak belajar',
    progress: 71,
    icon: TrendingUp,
    color: 'green'
  }
];


{/* Main Content */}
<div className="flex-1 p-8 ml-64">
  <div className="max-w-5xl mx-auto">
    {/* Header tetap sama */}
    
    {/* Tambahkan Quick Stats di sini */}
    <motion.div 
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.1 }}
      className="grid grid-cols-4 gap-6 mb-8"
    >
      {learningStats.map((stat, index) => (
        <div key={index} className="p-6 transition-all duration-300 border border-gray-700 bg-gray-800/50 backdrop-blur-sm rounded-xl hover:border-cyan-500/50">
          <div className="flex items-start justify-between">
            <div>
              <p className="mb-1 text-sm text-gray-400">{stat.label}</p>
              <p className="text-2xl font-bold text-white">{stat.value}</p>
            </div>
            <div className="p-2 rounded-lg bg-gradient-to-r from-cyan-500/10 to-blue-500/10">
              <stat.icon className="w-5 h-5 text-cyan-400" />
            </div>
          </div>
        </div>
      ))}
    </motion.div>

    {/* Setelah Current Course, tambahkan Quick Actions di sampingnya */}
    <div className="grid grid-cols-12 gap-6">
      {/* Current Course dalam grid */}
      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
        className="col-span-8"
      >
        {/* Current Course content Anda tetap sama */}
      </motion.div>

      {/* Quick Actions */}
      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
        className="col-span-4 space-y-6"
      >
        {quickActions.map((action, index) => (
          <motion.button
            key={index}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            className="w-full p-4 transition-all duration-300 border border-gray-700 bg-gray-800/50 backdrop-blur-sm rounded-xl hover:border-cyan-500/50"
          >
            <div className="flex items-center gap-4">
              <div className={`p-3 rounded-lg bg-${action.color}-500/10`}>
                <action.icon className={`w-6 h-6 text-${action.color}-400`} />
              </div>
              <div className="text-left">
                <p className="font-medium text-white">{action.label}</p>
                <p className="text-sm text-gray-400">{action.description}</p>
              </div>
            </div>
          </motion.button>
        ))}
      </motion.div>
    </div>

    {/* Di bawah Module List, tambahkan Achievement Section */}
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.4 }}
      className="mt-8"
    >
      <h2 className="mb-6 text-xl font-bold text-white">Pencapaian</h2>
      <div className="grid grid-cols-2 gap-6">
        {achievements.map((achievement, index) => (
          <div 
            key={index}
            className="p-6 transition-all duration-300 border border-gray-700 bg-gray-800/50 backdrop-blur-sm rounded-xl hover:border-cyan-500/50"
          >
            <div className="flex items-start gap-4 mb-4">
              <div className={`p-3 rounded-lg bg-${achievement.color}-500/10`}>
                <achievement.icon className={`w-6 h-6 text-${achievement.color}-400`} />
              </div>
              <div>
                <h3 className="font-medium text-white">{achievement.title}</h3>
                <p className="text-sm text-gray-400">{achievement.description}</p>
              </div>
            </div>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span className="text-gray-400">Progress</span>
                <span className="text-cyan-400">{achievement.progress}%</span>
              </div>
              <div className="h-2 bg-gray-700 rounded-full">
                <div 
                  className="h-2 rounded-full bg-gradient-to-r from-cyan-500 to-blue-500"
                  style={{ width: `${achievement.progress}%` }}
                ></div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </motion.div>
  </div>
</div>
